/**
 * åˆ›å»ºè¡¨æƒ…é€‰æ‹©å™¨é¢æ¿
 */
function createEmojiPicker(onSelect: (emoji: string) => void): HTMLElement {
  const picker = document.createElement('div')
  picker.className = 'ldesign-emoji-picker'
  picker.style.cssText = `
    position: fixed;
    background: white;
    border: 1px solid #e5e7eb;
    border-radius: 8px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 8px;
    width: 380px;
    max-height: 320px;
    overflow-y: auto;
    z-index: 10000;
    animation: fadeIn 0.2s ease-out;
  `

  // åˆ›å»ºåˆ†ç±»æ ‡ç­¾
  const tabs = document.createElement('div')
  tabs.style.cssText = `
    display: flex;
    gap: 8px;
    margin-bottom: 8px;
    padding-bottom: 8px;
    border-bottom: 1px solid #e5e7eb;
    flex-wrap: wrap;
  `

  // åˆ›å»ºè¡¨æƒ…å®¹å™¨
  const emojiContainer = document.createElement('div')
  emojiContainer.style.cssText = `
    display: grid;
    grid-template-columns: repeat(8, 1fr);
    gap: 4px;
  `

  // åˆ›å»ºåˆ†ç±»æŒ‰é’®å¹¶æ·»åŠ è¡¨æƒ…
  Object.entries(EMOJI_CATEGORIES).forEach(([key, category], index) => {
    const tab = document.createElement('button')
    tab.textContent = category.name
    tab.style.cssText = `
      padding: 4px 12px;
      border: none;
      background: ${index === 0 ? '#4f46e5' : '#f3f4f6'};
      color: ${index === 0 ? 'white' : '#374151'};
      border-radius: 4px;
      cursor: pointer;
      font-size: 12px;
      transition: all 0.2s;
    `

    tab.onmouseover = () => {
      if (tab.style.backgroundColor !== 'rgb(79, 70, 229)')
        tab.style.backgroundColor = '#e5e7eb'
    }

    tab.onmouseout = () => {
      if (tab.style.backgroundColor !== 'rgb(79, 70, 229)')
        tab.style.backgroundColor = '#f3f4f6'
    }

    tab.onclick = () => {
      // æ›´æ–°é€‰ä¸­çŠ¶æ€
      tabs.querySelectorAll('button').forEach((btn) => {
        btn.style.backgroundColor = '#f3f4f6'
        btn.style.color = '#374151'
      })
      tab.style.backgroundColor = '#4f46e5'
      tab.style.color = 'white'

      // æ˜¾ç¤ºå¯¹åº”åˆ†ç±»çš„è¡¨æƒ…
      emojiContainer.innerHTML = ''
      category.emojis.forEach((emoji) => {
        const emojiBtn = document.createElement('button')
        emojiBtn.textContent = emoji
        emojiBtn.style.cssText = `
          width: 36px;
          height: 36px;
          border: none;
          background: transparent;
          cursor: pointer;
          font-size: 20px;
          line-height: 1;
          border-radius: 4px;
          transition: background 0.2s;
        `

        emojiBtn.onmouseover = () => {
          emojiBtn.style.backgroundColor = '#f3f4f6'
        }

        emojiBtn.onmouseout = () => {
          emojiBtn.style.backgroundColor = 'transparent'
        }

        emojiBtn.onclick = () => {
          onSelect(emoji)
          picker.remove()
        }

        emojiContainer.appendChild(emojiBtn)
      })
    }

    tabs.appendChild(tab)

    // é»˜è®¤æ˜¾ç¤ºç¬¬ä¸€ä¸ªåˆ†ç±»
    if (index === 0)
      tab.click()
  })

  picker.appendChild(tabs)
  picker.appendChild(emojiContainer)

  // ç‚¹å‡»å¤–éƒ¨å…³é—­
  setTimeout(() => {
    const closeOnClickOutside = (e: MouseEvent) => {
      const target = e.target as Node
      const emojiButton = document.querySelector('[data-name="emoji"]')
      // å¦‚æœç‚¹å‡»çš„æ˜¯è¡¨æƒ…æŒ‰é’®æœ¬èº«ï¼Œä¸å…³é—­
      if (emojiButton && emojiButton.contains(target))
        return

      // å¦‚æœç‚¹å‡»çš„ä¸æ˜¯pickerå†…éƒ¨ï¼Œåˆ™å…³é—­
      if (!picker.contains(target)) {
        picker.remove()
        document.removeEventListener('click', closeOnClickOutside)
      }
    }
    document.addEventListener('click', closeOnClickOutside)
  }, 100) // ç¨å¾®å»¶è¿Ÿä»¥é¿å…ç«‹å³è§¦å‘

  return picker
}

/**
 * æ’å…¥è¡¨æƒ…å‘½ä»¤
 */
const insertEmoji: Command = (state, dispatch) => {
  if (!dispatch)
    return true

  // æ£€æŸ¥æ˜¯å¦å·²ç»æœ‰æ‰“å¼€çš„è¡¨æƒ…é€‰æ‹©å™¨
  const existingPicker = document.querySelector('.ldesign-emoji-picker')
  if (existingPicker) {
    existingPicker.remove()
    return true
  }

  const selection = window.getSelection()
  if (!selection || selection.rangeCount === 0)
    return false

  // ä¿å­˜å½“å‰é€‰åŒº
  const range = selection.getRangeAt(0)

  // åˆ›å»ºè¡¨æƒ…é€‰æ‹©å™¨
  const picker = createEmojiPicker((emoji) => {
    // æ¢å¤é€‰åŒºå¹¶æ’å…¥è¡¨æƒ…
    selection.removeAllRanges()
    selection.addRange(range)
    document.execCommand('insertText', false, emoji)
  })

  // è·å–å·¥å…·æ æŒ‰é’®ä½ç½® - ä½¿ç”¨äº‹ä»¶ç›®æ ‡è·å–å‡†ç¡®çš„æŒ‰é’®
  const button = (window.event?.target as HTMLElement)?.closest('[data-name="emoji"]')
    || document.querySelector('[data-name="emoji"]') as HTMLElement

  if (button) {
    const rect = button.getBoundingClientRect()
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop
    const scrollLeft = window.pageXOffset || document.documentElement.scrollLeft

    // è®¾ç½®ä½ç½®ä¸ºæŒ‰é’®ä¸‹æ–¹
    picker.style.position = 'fixed'
    picker.style.top = `${rect.bottom + 5}px`
    picker.style.left = `${rect.left}px`

    // æ£€æŸ¥æ˜¯å¦è¶…å‡ºå³è¾¹ç•Œ
    setTimeout(() => {
      const pickerRect = picker.getBoundingClientRect()
      if (pickerRect.right > window.innerWidth)
        picker.style.left = `${window.innerWidth - pickerRect.width - 10}px`

      // æ£€æŸ¥æ˜¯å¦è¶…å‡ºä¸‹è¾¹ç•Œ
      if (pickerRect.bottom > window.innerHeight) {
        // æ˜¾ç¤ºåœ¨æŒ‰é’®ä¸Šæ–¹
        picker.style.top = `${rect.top - pickerRect.height - 5}px`
      }
    }, 0)
  }

  document.body.appendChild(picker)

  return true
}

